import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    from: '', to: '', date: '', seatClass: 'economy',
  });

  const handleSearch = (e) => {
    e.preventDefault();
    const params = new URLSearchParams(form).toString();
    navigate(`/search?${params}`);
  };

  return (
    <>
      {/* Hero */}
      <div className="ff-hero">
        <div className="container text-center">
          <h1>‚úàÔ∏è Find Your Perfect Flight</h1>
          <p className="mt-2">Search hundreds of airlines. Book in seconds. Fly with confidence.</p>
        </div>
      </div>

      <div className="container">
        {/* Search Card */}
        <div className="ff-search-card mb-5">
          <h5 className="fw-bold mb-4" style={{ color: '#0369a1' }}>
            üîç Search Flights
          </h5>
          <form onSubmit={handleSearch}>
            <div className="row g-3">
              <div className="col-md-3">
                <label className="form-label fw-semibold small">From</label>
                <input
                  className="form-control ff-input"
                  placeholder="e.g. New York"
                  value={form.from}
                  onChange={(e) => setForm({ ...form, from: e.target.value })}
                  required
                />
              </div>
              <div className="col-md-3">
                <label className="form-label fw-semibold small">To</label>
                <input
                  className="form-control ff-input"
                  placeholder="e.g. Paris"
                  value={form.to}
                  onChange={(e) => setForm({ ...form, to: e.target.value })}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label fw-semibold small">Date</label>
                <input
                  type="date"
                  className="form-control ff-input"
                  value={form.date}
                  onChange={(e) => setForm({ ...form, date: e.target.value })}
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>
              <div className="col-md-2">
                <label className="form-label fw-semibold small">Class</label>
                <select
                  className="form-select ff-input"
                  value={form.seatClass}
                  onChange={(e) => setForm({ ...form, seatClass: e.target.value })}
                >
                  <option value="economy">Economy</option>
                  <option value="business">Business</option>
                </select>
              </div>
              <div className="col-md-2 d-flex align-items-end">
                <button type="submit" className="btn btn-sky w-100" style={{ padding: '12px' }}>
                  Search ‚úàÔ∏è
                </button>
              </div>
            </div>
          </form>
        </div>

        {/* Features */}
        <div className="row g-4 mb-5">
          {[
            { icon: 'üõ°Ô∏è', title: 'Safe & Secure', desc: 'Your data and payments are 100% protected.' },
            { icon: 'üí∏', title: 'Best Prices', desc: 'We compare fares to get you the best deal.' },
            { icon: 'üåç', title: 'Global Coverage', desc: 'Flights to 500+ destinations worldwide.' },
            { icon: 'üì±', title: 'Easy Booking', desc: 'Book, manage and cancel in just a few clicks.' },
          ].map((f) => (
            <div key={f.title} className="col-md-3 col-sm-6">
              <div className="ff-stat-card h-100">
                <div style={{ fontSize: '2.5rem' }}>{f.icon}</div>
                <div className="fw-bold mt-2">{f.title}</div>
                <div className="stat-label mt-1">{f.desc}</div>
              </div>
            </div>
          ))}
        </div>

        {/* Popular Routes */}
        <h5 className="fw-bold mb-3" style={{ color: '#0369a1' }}>üî• Popular Routes</h5>
        <div className="row g-3 mb-5">
          {[
            { from: 'New York', to: 'London',    price: '$499' },
            { from: 'Dubai',    to: 'Singapore', price: '$320' },
            { from: 'Mumbai',   to: 'Delhi',     price: '$80'  },
            { from: 'Paris',    to: 'New York',  price: '$550' },
          ].map((r) => (
            <div key={r.from + r.to} className="col-md-3 col-sm-6">
              <div
                className="ff-flight-card h-100 text-center"
                style={{ cursor: 'pointer', borderLeft: '4px solid #f59e0b' }}
                onClick={() => navigate(`/search?from=${r.from}&to=${r.to}`)}
              >
                <div className="fw-bold">{r.from} ‚úà {r.to}</div>
                <div className="ff-price mt-2">{r.price}</div>
                <div className="text-muted small">Starting from</div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Footer */}
      <footer style={{ background: '#0c1a2e', color: 'rgba(255,255,255,0.6)', padding: '24px 0', textAlign: 'center' }}>
        <p className="mb-0">‚úàÔ∏è FlightFinder ‚Äî Your trusted travel companion</p>
      </footer>
    </>
  );
}
